void readFile(char* filename, char** string) {
    FILE* f = fopen(filename, "rb");
    if (!f) {
        printf("Cannot open the file\n");
        return;
    }
    fseek(f, 0, SEEK_END);
    size_t fsize = ftell(f);
    fseek(f, 0, SEEK_SET);

    *string = (char*)malloc(fsize + 1);
    if (!*string) {
        printf("Cannot allocate string buffer");
        return;
    }
    fread(*string, fsize, 1, f);
    fclose(f);

    (*string)[fsize] = 0;
}

